""" Function to apply filter on Client """
def stn_tsap_client_filter(df,type):
    if type=='STN':
        filter_df = df.filter(df.Client == 280)
        return filter_df
    elif type=='TSAP':
        filter_df = df.filter(df.Client == 480)
        return filter_df       
    else:
        filter_df = df
        return filter_df  

"""Function for column selecing and renaming"""
def select_and_rename_columns(df,columns):
    return df.select([f.col(c).alias(columns[c]) for c in columns.keys()])


